mapper.update:

UPDATE   t_zfang_search_keys
SET   key_status = '1'
WHERE  device_id = #{deviceId};


UPDATE   t_zfang_search_keys
SET   key_status = '1'
WHERE  user_id = #{userId};

mapper.delete:
delete from t_esp_search_keys where  1 = 1

	  and user_id = #{userId} 


	  and device_id = #{deviceId}
